import React, { useEffect, useState } from 'react';
import ReactDom from 'react-dom'; 


function Status(props){
    // console.log('the props', props.data.length);
    const [activeCounter, setActiveCounter]= useState(0);
    const [inactiveCounter, setInactiveCounter]= useState(0);
    const [inShop, setInShop] = useState(0);



    let statusCounter = ()=>{

        for(let i=0; i < props.data.length; i++){
            // console.log('the props iii', props.data[i])
            if(props.data[i].status === "Active"){
                setActiveCounter(activeCounter+1)
            }
            else if(props.data[i].status === "Inactive"){
                setInactiveCounter(inactiveCounter+1)
            }
            else {
                setInShop(inShop+1)
            }
        }
    }

    useEffect(()=>{
        statusCounter()
    },[])

    return(
        <>
        <h4>Active : {activeCounter}</h4>
        {/* {props.data.map((element, idx)=>
        <li key={idx}>{element.status}</li>
        )} */}
        </>
    )
}

export default Status;
