import React, { useEffect, useState } from 'react';
import ReactDom from 'react-dom'; 
import './status.css'
import {CanvasJSChart} from 'canvasjs-react-charts'

function Fuel(props){
console.log('from the fuel', props.data)
  const fuelAvg = (j)=>{
    let sum = 0
    for(let i=0; i < props.data.length; i++){
      sum =+ props.data[i].weeklyFuel[j]
    }
    return sum/4
  }
   let one = fuelAvg(0)
  const options = {
    animationEnabled: true,
    title: {
      text: "Fuel Cost"
    },
    axisY: {
      title: "AVG/100km",
      // suffix: " kWh"
    },
    data: [{
      type: "splineArea",
      xValueFormatString: "YYYY",
      yValueFormatString: "#,##0.## bn kW⋅h",
      showInLegend: true,
      legendText: "kWh = one kilowatt hour",
      dataPoints: [
        { x: new Date(2021,1), y: `${one}` },
        { x: new Date(2021,2), y: fuelAvg(2) },
        { x: new Date(2021,3), y: 72.569 },
        { x: new Date(2021,4), y: 72.743 },
        { x: new Date(2021,5), y: 72.381 },
        { x: new Date(2021,6), y: 71.406 },

      ]
    }]
  }
    return(
        <>
        <div>
			<CanvasJSChart options = {options}
				/* onRef={ref => this.chart = ref} */
			/>
			{/*You can get reference to the chart instance as shown above using onRef. This allows you to access all chart properties and methods*/}
		</div>
        </>
    )
}

export default Fuel;
